import React, {useCallback, useEffect, useState} from 'react';
import SuperHeaderTag from "./SuperHeaderTag";

const SuperHeader = React.memo(function({columns, headerSort, setSortedTag}){

    // const [value, setValue] = useState('');

    const onClickHeaderSort = useCallback((label)=>()=>{
        console.log(label);
        setSortedTag(label);
    }, []);

    return <thead>{/*div header*/}
        <tr>{/*real header*/}
            {(columns.length > 0 &&
                columns.map((label, index) => <SuperHeaderTag key={index}
                                                              className={'header '+label}
                                                              onClick={onClickHeaderSort}
                                                              label={label}>{label}</SuperHeaderTag>)) || <th>header</th>}{/*каждая колонка*/}
        </tr>
    </thead>
});

export default SuperHeader;
