import React, {useEffect, useState} from 'react';

const SuperItemAdded = React.memo(() => {


    // TODO После заполнения всех инпутов активируется кнопка Добавить в таблицу которая вставляет заполненный ряд в начало таблицы

    const [isFormHidden, setIsFromHidden] = useState(false);

    const [newItem, setNewItem] = useState({
        id: '',
        firstName: '',
        lastName: '',
        email: '',
        phone: ''
    });

    const [validator, setValidator] = useState({
        id: true,
        firstName: true,
        lastName: true,
        email: true,
        phone: true
    });

    useEffect(() => {
        for(let prop in validator){
            if (validator[prop] === false) {
                document.getElementsByClassName('add_table button_submit').disabled = true;
                console.log(false);
                return;
            }
        }
        document.getElementsByClassName('add_table button_submit').disabled = false;
        console.log(document.getElementsByClassName('add_table button_submit'));
        console.log(true);
    },[newItem]);

    const onAddClick = () => {
        console.log('add');
        setIsFromHidden(true);
    }

    const onSubmitClick = () => {
        console.log('submit');
        console.log(newItem);
        setIsFromHidden(false);
    }

    const onCancelClick = () => {
        console.log('cancel');
        setIsFromHidden(false);
    }


    return <div>
        <button className="add_button button_add" onClick={onAddClick}>Add item in table</button>
        {isFormHidden && <table className="add_table table">
            <tbody>
            <tr>
                <td>id</td>
                <td><input onChange={(e) => {setNewItem({...newItem, id: e.target.value});}}/></td>
                {!validator.id && <td>Заполните id цифрами!</td>}
            </tr>
            <tr>
                <td>firstName</td>
                <td><input onChange={(e) => {setNewItem({...newItem, firstName: e.target.value});}}/></td>
                {!validator.firstName && <td>Заполните имя латинскими или русскими буквами!</td>}
            </tr>
            <tr>
                <td>lastName</td>
                <td><input onChange={(e) => {setNewItem({...newItem, lastName: e.target.value});}}/></td>
                {!validator.lastName && <td>Заполните фимилию латинскими или русскими буквами!</td>}
            </tr>
            <tr>
                <td>email</td>
                <td><input onChange={(e) => {setNewItem({...newItem, email: e.target.value});}}/></td>
                {!validator.email && <td>Заполните email латинскими буквами в форме ***@***.***!</td>}
            </tr>
            <tr>
                <td>phone</td>
                <td><input onChange={(e) => {setNewItem({...newItem, phone: e.target.value});}}/></td>
                {!validator.phone && <td>Заполните телефон 11-ю цифрами!</td>}
            </tr>
            </tbody>
        </table>}
        {isFormHidden && <button className="add_table button_submit"
                                 onClick={onSubmitClick}
                                 disabled={false}>Add item</button>}
        {isFormHidden && <button className="add_table button_cancel"
                                 onClick={onCancelClick}>Cancel</button>}
    </div>
});

export default SuperItemAdded;
