import React, {useCallback, useEffect, useState} from 'react';
import SuperHeader from './SuperHeader'
import SuperBody from './SuperBody'

const SuperTable = React.memo(function(props){

    const [curData, setCurData] = useState([]);
    const [columns, setColumns] = useState([]);
    const [sortedTag, setSortedTag] = useState('');

    // save data after receiving request
    useEffect(()=>{
        if(props.data !== [] && props.data[0] !== undefined){
            let tempColumns = []
            for(let tempProps in props.data[0]){
                tempColumns.push(tempProps);
            }
            setColumns(tempColumns);
            setCurData(props.data);
        }
    },[props]);

    // save data after sort
    useEffect(()=>{
        console.log(curData);
    },[curData,setCurData]);

    // sorting callback func
    useEffect(() => {
        console.log('sort');
        const tempData = curData.sort((a,b)=> a[sortedTag] < b[sortedTag] ? 1 : a[sortedTag] > b[sortedTag] ? -1 : 0);

        if(sortedTag.includes('+')) setCurData(tempData.reverse());
        else setCurData(tempData);
    }, [curData, sortedTag]);

    return <table>
        {sortedTag}
        <SuperHeader columns={columns} sortedTag={sortedTag} setSortedTag={setSortedTag}/>
        <SuperBody data={curData} columns={columns}/>
    </table>
});

export default SuperTable;
