import React, {useEffect, useState} from 'react';

const SuperItemAdded = React.memo(() => {


    // TODO После заполнения всех инпутов активируется кнопка Добавить в таблицу которая вставляет заполненный ряд в начало таблицы

    const [isFormHidden, setIsFromHidden] = useState(false);

    let inputId = <input/>;
    let inputFirstName = <input/>;
    let inputLastName = <input/>;
    let inputEmail = <input/>;
    let inputPhone = <input/>;

    const onAddClick = () => {
        console.log('add');
        setIsFromHidden(true);
    }

    const onSubmitClick = () => {
        let inputId_value = document.getElementsByClassName('inputId')[0];
        console.log('submit');
        console.log(inputId_value);
        console.log([inputFirstName, inputLastName, inputEmail, inputPhone]);
        setIsFromHidden(false);
    }

    const onCancelClick = () => {
        console.log('cancel');
        setIsFromHidden(false);
    }

    useEffect(()=>{
        console.log('change');
    },[inputId.value, inputFirstName.value, inputLastName.value, inputEmail.value, inputPhone.value])

    return <div>
        <button className="add_button button_add" onClick={onAddClick}>Add item in table</button>
        {isFormHidden && <table className="add_table table">
            <tbody>
            <tr>
                <td>id</td>
                <td><input className={inputId}/></td>
            </tr>
            <tr>
                <td>firstName</td>
                <td>{inputFirstName}</td>
            </tr>
            <tr>
                <td>lastName</td>
                <td>{inputLastName}</td>
            </tr>
            <tr>
                <td>email</td>
                <td>{inputEmail}</td>
            </tr>
            <tr>
                <td>phone</td>
                <td>{inputPhone}</td>
            </tr>
            </tbody>
        </table>}
        {isFormHidden && <button className="add_table button_submit" onClick={onSubmitClick}>Add item</button>}
        {isFormHidden && <button className="add_table button_cancel" onClick={onCancelClick}>Cancel</button>}
    </div>
});

export default SuperItemAdded;
