import React, {useCallback, useEffect, useState} from 'react';

const SuperHeader = React.memo(function({columns, headerSort, setSortedTag}){

    // const [value, setValue] = useState('');

    const onClickHeaderSort = useCallback((label)=>{
        console.log(label);
        setSortedTag(label);
    }, []);

    return <thead>{/*div header*/}
        <tr>{/*real header*/}
            {(columns.length > 0 && columns.map((label, index) => <th key={index} onClick={onClickHeaderSort}>{label}</th>)) || <th>header</th>}{/*каждая колонка*/}
        </tr>
    </thead>
});

export default SuperHeader;
