import React, {useEffect} from 'react';


const SuperBody = React.memo(function({curData, sortedTag, setCurData}){

    // save data after sort
    useEffect(() => {
        console.log(sortedTag);
        //const tempData = ;
        if(sortedTag && sortedTag.includes('+')) {
            setCurData([...curData.reverse()]);
        }
        else{
            setCurData([...curData.sort((a, b) => a[sortedTag] < b[sortedTag] ? -1 : a[sortedTag] > b[sortedTag] ? 1 : 0)]);
        }
    }, [sortedTag]);


    function dataTablePresentation(curData){
        console.log('body');
        if(curData && curData.length > 0){
            let resultArr = [];
            for(let i = 0; i < curData.length; i++){
                let tempRow = [];
                let propCounter = 0;
                for(let prop in curData[i]){
                    if(prop !== 'address') tempRow.push(<td className={'column '+prop} key={propCounter++}>{curData[i][prop]}</td>);
                    else if(prop === 'address') {
                        // {streetAddress, city, state, zip}
                        let tempAddress = '';
                        for(let position in curData[i][prop]){
                            tempAddress += curData[i][prop][position] + ' ';
                        }
                        tempRow.push(<td className={'column '+prop} key={propCounter++}>{tempAddress}</td>);
                    }
                }
                resultArr.push(<tr className={'row '+i} key={i}>{tempRow}</tr>)
            }
            return resultArr
        } else {
            return <tr><td>empty data</td></tr>;
        }
        }

    return <tbody>{/*div body*/}
        <tr><td>{sortedTag}</td></tr>
        {dataTablePresentation(curData) || <tr><td>body</td></tr>}
    </tbody>
});

export default SuperBody;
