import React, {useCallback, useState} from 'react';

const SuperHeaderTag = React.memo(function({index, class_name, sortedTag, setSortedTag, label}){

    let [shownLabel, setShownLabel] = useState('');

    let  onClickHeaderSort = useCallback((label) => {
        if (sortedTag === label) {
            setSortedTag('+' + label);
            setShownLabel('+')
        } else if (sortedTag === '+'+label){
            setSortedTag(label);
            setShownLabel('-')
        } else {
            setShownLabel('')
        }
    }, [sortedTag, setSortedTag]);

    onClickHeaderSort = onClickHeaderSort.bind(undefined, label);

    return <th key={index} className={class_name} onClick={onClickHeaderSort}>
        <div>{label}</div>
        {shownLabel === '' ? '' :
            shownLabel === '+' ? '\u2B9D' : '\u2B9F'}
    </th>
});

export default SuperHeaderTag;
