import React, {useEffect, useState} from 'react';
import './SuperItemAdded.css';

const SuperItemAdded = React.memo(() => {

    const [isFormHidden, setIsFromHidden] = useState(false);
    useEffect(() => {
        const table = document.getElementsByClassName('add_table');

        for (let element in table) {
            table[element].style = {hidden: isFormHidden}
        }

    }, [isFormHidden]);

    const onAddClick = () => {
        console.log('add');
        setIsFromHidden(true);
    }

    const onSubmitClick = () => {
        console.log('submit');
        setIsFromHidden(false);
    }

    const onCancelClick = () => {
        console.log('cancel');
        setIsFromHidden(false);
    }

    return <div>
        <button className="add_button button_add" onClick={onAddClick}>Add item in table</button>
        <table className="add_table table">
            <tbody>
            <tr>
                <td>id</td>
                <td><input/></td>
            </tr>
            <tr>
                <td>firstName</td>
                <td><input/></td>
            </tr>
            <tr>
                <td>lastName</td>
                <td><input/></td>
            </tr>
            <tr>
                <td>email</td>
                <td><input/></td>
            </tr>
            <tr>
                <td>phone</td>
                <td><input/></td>
            </tr>
            <tr>
                <td>address</td>
                <td><input/></td>
            </tr>
            <tr>
                <td>description</td>
                <td><input/></td>
            </tr>
            </tbody>
        </table>
        <button className="add_table button_submit" onClick={onSubmitClick}>Add item</button>
        <button className="add_table button_cancel" onClick={onCancelClick}>Cancel</button>
    </div>
});

export default SuperItemAdded;
