import React, {useEffect, useState} from 'react';
import './SuperItemAdded.css';

const SuperItemAdded = React.memo(() => {

    const [isFormHidden, setIsFromHidden] = useState('hidden');
    useEffect(() => {
        const table = document.getElementsByClassName('add_table');
        console.log(table);
        for (let element in table) {
            if(table[element] === 'length'){ break;}
            console.log(element);
            console.log(table[element]);
            console.log(table[element].style['visibility']);
            table[element].style['visibility'] = isFormHidden;
        }

    }, [isFormHidden]);

    const onAddClick = () => {
        console.log('add');
        setIsFromHidden('visible');
    }

    const onSubmitClick = () => {
        console.log('submit');
        setIsFromHidden('hidden');
    }

    const onCancelClick = () => {
        console.log('cancel');
        setIsFromHidden('hidden');
    }

    return <div>
        <button className="add_button button_add" onClick={onAddClick}>Add item in table</button>
        <table className="add_table table">
            <tbody>
            <tr>
                <td>id</td>
                <td><input/></td>
            </tr>
            <tr>
                <td>firstName</td>
                <td><input/></td>
            </tr>
            <tr>
                <td>lastName</td>
                <td><input/></td>
            </tr>
            <tr>
                <td>email</td>
                <td><input/></td>
            </tr>
            <tr>
                <td>phone</td>
                <td><input/></td>
            </tr>
            <tr>
                <td>address</td>
                <td><input/></td>
            </tr>
            <tr>
                <td>description</td>
                <td><input/></td>
            </tr>
            </tbody>
        </table>
        <button className="add_table button_submit" onClick={onSubmitClick}>Add item</button>
        <button className="add_table button_cancel" onClick={onCancelClick}>Cancel</button>
    </div>
});

export default SuperItemAdded;
